mapper.select:
SELECT u.user_id userId, u.real_name realName, u.id_num idNum, u.username username, u.user_email userEmail, u.user_phone userPhone, u.user_address userAddress, u.password password, u.create_time createTime, u.ssex sex, u.update_time updateTime, u.description description, u.avatar avatar, u.status status, GROUP_CONCAT(r.role_id) roleId, GROUP_CONCAT(r.role_name) roleName FROM t_user u LEFT JOIN user_role ur ON (u.user_id = ur.user_id) LEFT JOIN role r ON r.role_id = ur.role_id WHERE 1 = 1
AND u.username = #{user.username}
AND u.ssex = #{user.sex}
AND u.status = #{user.status}
AND u.mobile = #{user.mobile}
group by u.username,u.user_id,u.real_name,u.id_num,u.user_email,u.user_phone,u.user_address,u.password, u.create_time,u.ssex,u.status, u.update_time,u.description,u.avatar
